import React, { useState } from "react";
import { Bar } from "react-chartjs-2";
import { FaSun, FaMoon } from "react-icons/fa";
import MoodGraph from "./components/MoodGraph";
import "./App.css";
 // Make sure to create App.css

function App() {
  const [theme, setTheme] = useState("light");
  const [memories, setMemories] = useState([]);
  const [input, setInput] = useState("");

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  const addMemory = () => {
    if (input) {
      setMemories([...memories, { text: input, mood: "Happy" }]);
      setInput("");
    }
  };

  const data = {
    labels: ["Happy", "Neutral", "Sad"],
    datasets: [
      {
        label: "Mood Count",
        data: [
          memories.filter((m) => m.mood === "Happy").length,
          memories.filter((m) => m.mood === "Neutral").length,
          memories.filter((m) => m.mood === "Sad").length,
        ],
        backgroundColor: ["#4caf50", "#ffc107", "#f44336"],
      },
    ],
  };

  return (
    <div className={`app ${theme}`}>
      <header className="header">
        <h1>Memory Lane</h1>
        <button className="theme-btn" onClick={toggleTheme}>
          {theme === "light" ? <FaMoon /> : <FaSun />}
        </button>
      </header>

      <div className="memory-input">
        <input
          type="text"
          placeholder="Write something..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button onClick={addMemory}>Add Memory</button>
      </div>

      <div className="timeline">
        {memories.length === 0 && <p>No memories yet.</p>}
        {memories.map((m, idx) => (
          <div className="memory-card" key={idx}>
            <p>{m.text}</p>
          </div>
        ))}
      </div>

      <div className="chart-container">
        <h2>Mood Tracker</h2>
        <Bar data={data} />
      </div>
    </div>
  );
}

export default App;
